package com.naver.controller;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

import org.springframework.http.HttpStatus;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RestController;

import com.naver.vo.SampleVO;

@RestController
/*스프링 4.0 이후부턴 해당 애노테이션 사용함으로써 
 * jsp와 같은 뷰페이지 만들지 않고도 REST 방식의 데이터 처리 가능
 * JSON(키&값 쌍의 사전적인 자료구조),XML,문자열 등 만들어짐
 */
@RequestMapping("/sample/*")
//컨트롤자체 매핑주소 등록
public class Sample6Controller {
	@RequestMapping("/rest_begin") // sample/rest_begin
	//get & post로 접근하는 매핑 주소처리, rest_begin 등록
	
	public String rest_begin() {
		return "REST API Start"; //문자열객체 반환
	}//rest_begin
	

	@RequestMapping(value="/sendVO", produces="application/json")
	public SampleVO sendVO() {
		//메서드 리턴 타입이 SampleVO이면 빈클래스 변수명이 JSON 데이터의 키이름이 된다
		SampleVO vo= new SampleVO();
		vo.setMno(10);
		vo.setFirstName("hong");
		vo.setLasrName("gilldong");
		return vo;
	}//sendVO end
	
	//컬렉션 List 타입의 JSON데이터 생성
	@RequestMapping(value="/sendList", produces="application/json")//sendList매핑주소
	public List<SampleVO> sendList(){
		
		List<SampleVO> list = new ArrayList<>();
		
		for(int i=0; i<=5; i++) {
			SampleVO vo= new SampleVO();
			vo.setMno(i);
			vo.setFirstName("sin");
			vo.setLasrName("sa");
			
			list.add(vo); //컬렉션의 추가                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          
		}
		
		return list;
	}
	
	//키 & 값 쌍의 JSON 데이터 -  MAP 타입
	@RequestMapping(value="/sendMap" ,produces="application/json")
	public Map<Integer, SampleVO> sendMap(){
		Map<Integer, SampleVO> map = new HashMap<>();
		for(int i=0; i<=5; i++) {
			SampleVO vo= new SampleVO();
			vo.setMno(i);
			vo.setFirstName("hong");
			vo.setLasrName("gilldong");
			map.put(i,vo); //컬렉션 맵에 키, 값 쌍으로 저장
		}
		return map;
	}//sendMap end
	
	//에러페이지 처리
	@RequestMapping("/sandError")
	public ResponseEntity<Void> sendError(){
		return new ResponseEntity<>(HttpStatus.BAD_REQUEST);
		/*
		  @RestController는 별도의 뷰페이지 없이 REST 서비스를 실행하기 때문에 
		  결과 데이터의 대한 오류가 많이 발생.
		  이런 경우에는 스프링에서 제공하는 내장 API인 ResponseEntity를 사용하여
		  개발자에게 문제가 되는 HTTP 에러 상태코드인 404,405,500 등의 에러를 
		  나쁜 상태코드를 데이터와 함께 브라우저로 전송해서 세밀한 에러 제어가 가능하다
		  
		 BAD_REQUEST : 400 에러코드 전송 
		 */
	}//sendError
	
	//컬렉션 List 제네릭 타입 JSON 정상적인 데이터와 404 에러(해당경로 파일을 찾을 수 없습니다)
	@RequestMapping(value="/sendListErrorNot" ,produces="application/json")
	public ResponseEntity<List<SampleVO>> sendListNot(){
		List<SampleVO> list = new ArrayList<>();
		for(int i =3; i>=1; i--) {
			SampleVO vo = new SampleVO();
			vo.setMno(i);
			vo.setFirstName("Lee");
			vo.setLasrName("sunsin");
			
			list.add(vo);
		}
		return new  ResponseEntity<List<SampleVO>>(list, HttpStatus.NOT_FOUND);
		//NOT_FOUND : 404 에러 상태코드 
		
	}
	
	
	
}
